USE CSMovimientoGranos
GO

SELECT Movimiento_Cereal_Analisis.MuestraNumero AS CodigoMuestra, Entidad_Destino.CUIT AS CUIT_Destino, 'CUALITATIVO' AS TipoTesteo, 'INTACTA' AS TecnologiaTesteada, (CASE Movimiento_Cereal_Analisis.ResultadoIPRO WHEN 1 THEN 'POSITIVO' ELSE 'NEGATIVO' END) AS ResultadoCualitativo, NULL AS ResultadoCuantitativo, CONVERT(char(10), Movimiento_Cereal_Analisis.Fecha, 103) AS FechaTesteo, (CASE Movimiento_Cereal_Analisis.ResultadoIPRO WHEN 1 THEN REPLACE(Entidad_RemitenteComercial.Nombre, ',', '') ELSE NULL END) AS RemitenteComercial, (CASE Movimiento_Cereal_Analisis.ResultadoIPRO WHEN 1 THEN Entidad_RemitenteComercial.CUIT ELSE NULL END) AS CUIT_RemitenteComercial, (CASE Movimiento_Cereal_Analisis.ResultadoIPRO WHEN 1 THEN REPLACE(Entidad_Titular.Nombre, ',', '') ELSE NULL END) AS Titular, (CASE Movimiento_Cereal_Analisis.ResultadoIPRO WHEN 1 THEN Entidad_Titular.CUIT ELSE NULL END) AS CUIT_Titular
	FROM (((Movimiento_Cereal INNER JOIN Entidad AS Entidad_Destino ON Movimiento_Cereal.IDEntidad_Destino = Entidad_Destino.IDEntidad) INNER JOIN Entidad AS Entidad_Titular ON Movimiento_Cereal.IDEntidad_Titular = Entidad_Titular.IDEntidad) LEFT JOIN Entidad AS Entidad_RemitenteComercial ON Movimiento_Cereal.IDEntidad_RemitenteComercial = Entidad_RemitenteComercial.IDEntidad) INNER JOIN Movimiento_Cereal_Analisis ON Movimiento_Cereal.IDMovimiento_Cereal = Movimiento_Cereal_Analisis.IDMovimiento_Cereal
	WHERE Movimiento_Cereal.Tipo = 'E' AND Movimiento_Cereal.IDOrigenDestino_Destino = 1 AND Movimiento_Cereal.IDCereal = 3 AND Movimiento_Cereal.IDCosecha = 16